var email_list=[];
var grApp = new GlideRecord('sysapproval_approver');
grApp.addQuery('sysapproval',current.sys_id);
grApp.query();
while(grApp.next())
	{
	email_list.push(grApp.approver.email);
}
if(current.assignment_group != ''){
var emaillist = new GlideRecord('sys_user_grmember');
emaillist.addQuery('group',current.assignment_group);
emaillist.query();
while(emaillist.next())
	{
	email_list.push(emaillist.user.email);
}
}

gs.eventQueue("x_vocr_ce.request.rejected",grApp,grApp.sysapproval.u_requested_for_ssp,email_list);
//gs.eventQueue("x_vocr_ce.request.approved", current, current.assignment_group);

