var gr = new GlideRecord("sysapproval_approver");
gr.addEncodedQuery('sysapproval='+current.sys_id+'^state=approved^wf_activity.stage.nameSTARTSWITHCompliance and Ethics Verification');
gr.query();
if (gr.next()) {
   current.assigned_to=gr.approver;
	
}

